package Day4;

public class PatterrnTriangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		 System.out.println("7:- Triangle Pattern using Number: ");
		 int i, j, n=5 , count = 1;
	        for (i=1;i<=n;i++){
	            for (j=1;j<=i;j++){
	                System.out.print(count+" ");
	               count=count+1;
	            }
	            System.out.println();
	        }
	
	}

}
