package Day4;

public class TriangleUsingNumberPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("6:-Triangle Pattern using Number: ");
		int i,j,n=5;
        for (i=1;i<=n;i++){
            for (j=1;j<=i;j++){
                System.out.print(j+" ");
            }
            System.out.println();
        }
       
	
	}

}
