public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = -50;
		if (num>0) {
			System.out.println("Positive number");
			}
		else if(num<0) {
			System.out.println("Negativetive number");
		}
		else {
			System.out.println("Number is zero");
		}
	}

}
